import {Link} from "react-router-dom";

function Photo(props) {
    const viewDetails = () => {
        const data = {
            photoId: props.photo._id,
            username: props.photo.postedBy.username,
            postedBy: props.photo.postedBy._id,
            title: props.photo.name,
            path: props.photo.path,
            views: props.photo.views,
            likes: props.photo.likes,
        }

    }

    return (
        <div className="row border mb-4 p-3">
            <div className="col-4">
                <img
                    className="img-fluid"
                    src={`http://localhost:3001/${props.photo.path}`}
                    alt={props.photo.name}
                    style={{width: "100%", height: "auto", border: "1px solid gray"}}
                />
            </div>
            <div className="col-8">
                <div className="row">
                    <div className="col-12">
                        <h4 className="fw-bold">{props.photo.name}</h4>
                    </div>
                </div>
                <div className="row mb-2">
                    <div className="col-6">
                        <small className="text-muted">
                            Uploaded by: {props.photo.postedBy.username}
                        </small>
                    </div>
                    <div className="col-6 text-end">
                        <small className="text-muted ml-2">
                            Likes: {props.photo.likes}
                        </small>
                    </div>
                </div>
                <div className="row">
                    <div className="col-12">
                        <small className="text-muted">
                            {new Date(props.photo.postedAt).toLocaleString(undefined, {
                                dateStyle: "short",
                                timeStyle: "short",
                            })}
                        </small>
                    </div>
                </div>
                <div className="row mt-2">
                    <div className="col-12">
                        <Link
                            to={`/photos/${props.photo._id}`}
                            className="btn btn-info btn-sm"
                        >
                            View Details
                        </Link>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Photo;
