import {useParams} from "react-router-dom";
import {useEffect, useState} from "react";

function Details() {
    const {id} = useParams();
    const [photo, setPhoto] = useState(null);

    useEffect(() => {
        const fetchPhoto = async () => {
            try {
                const res = await fetch(`http://localhost:3001/photos/${id}`);
                const data = await res.json();
                setPhoto(data);
            } catch (e) {
                console.error(e);
            }
        };
        fetchPhoto();
    }, [id]);

    return (
        <div className="container my-5">
            {photo ? (
                <div className="row">
                    <div className="col-md-6">
                        <h1>{photo.name}</h1>
                        <div className="d-flex align-items-start">
                            <img
                                src={`http://localhost:3001/${photo.path}`}
                                alt={photo.name}
                                className="img-fluid rounded mt-4 mb-4"
                                style={{maxHeight: "600px"}}
                            />
                            <div className="ml-4 mt-4">
                                <h3>Posted by: {photo.postedBy.username}</h3>
                                <h3>Likes: {photo.likes}</h3>
                                <p>Views: {photo.views}</p>
                                <p>Posted at: {new Date(photo.postedAt).toLocaleString()}</p>
                            </div>
                        </div>
                        <p className="mt-4">{photo.description}</p>
                    </div>
                </div>
            ) : (
                <h1>Loading...</h1>
            )}
        </div>
    );
}

export default Details;
