import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import Card from "react-bootstrap/Card";

function Details() {
    const { id } = useParams();
    const [photo, setPhoto] = useState(null);

    useEffect(() => {
        const fetchPhoto = async () => {
            try {
                const res = await fetch(`http://localhost:3001/photos/${id}`);
                const data = await res.json();
                setPhoto(data);
            } catch (e) {
                console.error(e);
            }
        };
        fetchPhoto();
    }, [id]);

    return (
        <div className="container">
            {photo ? (
                <Card className="mt-4">
                    <Card.Img variant="top" src={`http://localhost:3001/${photo.path}`} alt={photo.name} style={{ maxHeight: "600px" }} />
                    <Card.Body>
                        <Card.Title>{photo.name}</Card.Title>
                        <Card.Text>
                            <p className="mb-1">Posted by: {photo.postedBy.username}</p>
                            <p className="mb-1">Likes: {photo.likes}</p>
                            <p className="mb-1">Views: {photo.views}</p>
                            <p className="mb-1">Posted at: {new Date(photo.postedAt).toLocaleString()}</p>
                            <p className="mt-3">{photo.description}</p>
                        </Card.Text>
                    </Card.Body>
                </Card>
            ) : (
                <h1>Loading...</h1>
            )}
        </div>
    );
}

export default Details;
