import {useParams} from "react-router-dom";
import {useEffect, useState} from "react";

function Details() {
    const {id} = useParams();
    const [photo, setPhoto] = useState(null);

    useEffect(() => {
        const fetchPhoto = async () => {
            try {
                const res = await fetch(`http://localhost:3001/photos/${id}`);
                const data = await res.json();
                setPhoto(data);
            } catch (e) {
                console.error(e);
            }
        };
        fetchPhoto().then((r) => console.log(r));
    }, [id]);

    return (
        <div className="container">
            {photo ? (
                <>
                    <h1>{photo.name}</h1>
                    <img
                        src={`http://localhost:3001/${photo.path}`}
                        alt={photo.name}
                        className="img-fluid rounded mt-4 mb-4"
                        style={{maxHeight: "600px"}}
                    />
                    <div className="row">
                        <div className="col-6">
                            <h5>Posted by: {photo.postedBy.username}</h5>
                        </div>
                        <div className="col-6 text-end">
                            <h5>Likes: {photo.likes}</h5>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-12">
                            <p className="mt-4">{photo.description}</p>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-12">
                            <small className="text-muted">
                                {new Date(photo.postedAt).toLocaleString(undefined, {
                                    dateStyle: "short",
                                    timeStyle: "short",
                                })}
                            </small>
                        </div>
                    </div>
                </>
            ) : (
                <h1>Loading...</h1>
            )}
        </div>
    );
}

export default Details;
