import {useParams} from "react-router-dom";
import {useEffect, useState} from "react";

function Details() {
    const {id} = useParams();
    const [photo, setPhoto] = useState(null);
    const [isOpen, setIsOpen] = useState(false);
    const [comment, setComment] = useState("");

    useEffect(() => {
        const fetchPhoto = async () => {
            try {
                const res = await fetch(`http://localhost:3001/photos/${id}`);
                const data = await res.json();
                setPhoto(data);
            } catch (e) {
                console.error(e);
            }
        };
        fetchPhoto();
    }, [id]);

    const openCommentField = () => {
        setIsOpen(current => !current);
    };
    const handleSubmit = async (e) => {
        e.preventDefault();

        const res = await fetch(`http://localhost:3001/photos/${id}/comments`, {
            method: "POST",
            credentials: "include",
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                content: comment,
            })
        });
        const data = await res.json();
        setComment("");
        setIsOpen(false);
    };

    return (
        <div className="container my-5">
            {photo ? (
                <div className="row">
                    <div className="col-md-6">
                        <img
                            src={`http://localhost:3001/${photo.path}`}
                            alt={photo.name}
                            className="img-fluid rounded mt-4 mb-4"
                            style={{maxHeight: "600px"}}
                        />
                    </div>
                    <div className="col-md-6">
                        <h1 className="mt-4">{photo.name}</h1>
                        <p>Posted by: {photo.postedBy.username}</p>
                        <p>Likes: {photo.likes}</p>
                        <p>Views: {photo.views}</p>
                        <p>Posted at: {new Date(photo.postedAt).toLocaleString()}</p>
                        <p className="mt-4">{photo.description}</p>
                        <button className="btn btn-dark" onClick={openCommentField}>
                            Add Comment
                        </button>
                        {isOpen && (
                            <form onSubmit={handleSubmit}>
                                <div className="form-group">
                                    <label htmlFor="commentInput">Comment</label>
                                    <textarea
                                        className="form-control"
                                        id="commentInput"
                                        rows="3"
                                        value={comment}
                                        onChange={(e) => setComment(e.target.value)}
                                    />
                                </div>
                                <button type="submit" className="btn btn-success mt-3">
                                    Submit
                                </button>
                            </form>
                        )}
                    </div>
                </div>
            ) : (
                <h1>Loading...</h1>
            )}
        </div>
    );
}

export default Details;
