function Comment(props) {
    return (
        <div className="card my-3">
            <div className="card-body">
                <p className="card-text">{props.comment.content}</p>
                <small className="card-subtitle text-muted">Posted
                    on {new Date(props.comment.postedAt).toLocaleString()}</small>
                <small className="card-subtitle text-muted"> Posted by {props.comment.postedBy.username}</small>
            </div>
        </div>

    )
}

export default Comment;
